mapper.select:
SELECT id, pic_url AS picUrl, insert_user AS insertUser, DATE_FORMAT(insert_time, '%Y-%m-%d %T') AS insertTimeStr, DATE_FORMAT(update_time, '%Y-%m-%d %T') AS updateTimeStr, status FROM qst_carousel WHERE 1=1
and status=#{status}
ORDER BY insert_time DESC LIMIT ${startIndex},${endIndex}

select count(*) from qst_carousel

	1=1 

		and status=#{status}
