mapper.select:
SELECT 

FROM front_company_report a

WHERE a.id = #{id}

SELECT 

FROM front_company_report a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.year = #{year}


		AND a.month = #{month}


		AND a.report_time = #{reportTime}


		AND a.company_id = #{companyId}


		AND a.area_id = #{area.id}


		AND a.total_profit = #{totalProfit}


		AND a.insert_time BETWEEN #{beginInsertTime} AND #{endInsertTime}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM front_company_report a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select date_format(r.insert_time, '%y/%m') as name, count( date_format(r.insert_time, '%y/%m') ) as count from front_company_report r where r.del_flag = 0
AND r.status = #{status}
and date_format(r.insert_time, '%y/%m') in
#{item}
group by date_format(r.insert_time, '%y/%m') order by date_format(r.insert_time, '%y/%m') desc

mapper.update:
UPDATE front_company_report SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
