mapper.select:
SELECT 

FROM front_notification a

WHERE a.id = #{id}

SELECT 

FROM front_notification a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.category_type = #{categoryType}


		AND (a.category_type = 1 or a.category_type = 2)


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.create_name like CONCAT(CONCAT('%', #{createName}), '%')


		AND date_format(a.create_date, '%Y/%m%d') =  date_format(#{createDate}, '%Y/%m%d') 


		AND (a.company_type = #{companyType } or a.company_type in (select id from sys_office where parent_ids like CONCAT(CONCAT('%', #{companyType}), '%')))


		AND a.keywords = #{keywords } 


		AND o5.id = #{companyId } 


		AND o5.area_id = #{area.id}


		AND (a.title like CONCAT(CONCAT('%', #{vagueWords}), '%')  OR a.content like CONCAT(CONCAT('%', #{vagueWords}), '%'))




		ORDER BY a.weight, ${page.orderBy} 


		ORDER BY a.weight, a.update_date DESC

SELECT 

FROM front_notification a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.weight, a.update_date DESC

mapper.update:
UPDATE front_notification SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
