SELECT SUM(total_income) as "totalIncome",SUM(total_profit) as "totalProfit",SUM(total_tax) as "totalTax",SUM(emp_quantity) as "empQuantity", SUM(employee_compensation) as "employeeCompensation",SUM(loan_amount) as "loanAmount",SUM(order_quantity) as "orderQuantity",SUM(operating_costs) as "operatingCosts", month as "month", year as "year" FROM statis_view
AND type_id = #{typeId}
AND area_name = #{areaName}
AND company_id = #{companyId}
GROUP BY year, month

SELECT
c.`month` AS name, c.areaName AS name, c.typeName AS name,
FORMAT((ntotalIncome - ptotalIncome) / ptotalIncome * 100,2) AS totalIncome, FORMAT((ntotalProfit - ptotalProfit) / ptotalProfit * 100,2) AS totalProfit, FORMAT((ntotalTax - ptotalTax) / ptotalTax * 100,2) AS totalTax, FORMAT((nempQuantity - pempQuantity) / pempQuantity * 100,2) AS empQuantity, FORMAT((nemployeeCompensation - pemployeeCompensation) / pemployeeCompensation * 100,2) AS employeeCompensation, FORMAT((nloanAmount - ploanAmount) / ploanAmount * 100,2) AS loanAmount, FORMAT((norderQuantity - porderQuantity) / porderQuantity * 100,2) AS orderQuantity, FORMAT((noperatingCosts - poperatingCosts) / poperatingCosts * 100,2) AS operatingCosts, FORMAT((nbankLoanAmount - pbankLoanAmount) / pbankLoanAmount * 100,2) AS bankLoanAmount, FORMAT((ntotalInvestment - ptotalInvestment) / ptotalInvestment * 100,2) AS totalInvestment FROM ( SELECT SUM(total_income) AS ntotalIncome, SUM(total_profit) AS ntotalProfit, SUM(total_tax) AS ntotalTax, SUM(emp_quantity) AS nempQuantity, SUM(employee_compensation) AS nemployeeCompensation, SUM(loan_amount) AS nloanAmount, SUM(order_quantity) AS norderQuantity, SUM(operating_costs) AS noperatingCosts, SUM(bank_loan_amount) AS nbankLoanAmount, SUM(total_investment) AS ntotalInvestment, MONTH AS "month", YEAR AS "year"
, area_name AS "areaName" , type_name AS "typeName"
FROM statis_view WHERE `year` = #{year} AND `month` <= #{month}
AND type_name = #{typeName}
AND area_name = #{areaName}
AND company_Name like CONCAT(CONCAT('%', #{companyName}), '%')
AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY
MONTH area_name type_name
) n RIGHT JOIN ( SELECT SUM(total_income) AS ptotalIncome, SUM(total_profit) AS ptotalProfit, SUM(total_tax) AS ptotalTax, SUM(emp_quantity) AS pempQuantity, SUM(employee_compensation) AS pemployeeCompensation, SUM(loan_amount) AS ploanAmount, SUM(order_quantity) AS porderQuantity, SUM(operating_costs) AS poperatingCosts, SUM(bank_loan_amount) AS pbankLoanAmount, SUM(total_investment) AS ptotalInvestment, MONTH AS "month", YEAR AS "year"
, area_name AS "areaName" , type_name AS "typeName"
FROM statis_view WHERE
`year` = #{preYear} AND `month` <= #{month} ( `year` = #{year} AND `month` < #{month} ) OR ( `year` = (#{year} - 1) AND `month` = '12')
AND type_name = #{typeName}
AND area_name = #{areaName}
AND company_Name like CONCAT(CONCAT('%', #{companyName}), '%')
AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY
MONTH area_name type_name
) p ON
n.`month` = p.`month` IF ( (n.`month` - 1) = 0, n.year - 1 = p.year AND (n.`month` + 11) = p.`month` , n.year = p.year AND (n.`month` - 1) = p.`month` ) n.`areaName` = p.`areaName` n.`typeName` = p.`typeName`
AND n.`areaName` = p.`areaName` AND n.`typeName` = p.`typeName`
RIGHT JOIN (
SELECT MONTH AS month FROM statis_view WHERE `year` = #{year} AND `month` <= #{month} AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY MONTH
SELECT name AS areaName FROM sys_area WHERE type != 1 AND type !=2
SELECT label AS typeName FROM sys_dict WHERE type=#{dictType}
) c ON
c.`month` = n.`month` c.`areaName` = n.`areaName` c.`typeName` = n.`typeName`

SELECT totalIncome, totalProfit, totalTax, empQuantity, employeeCompensation, loanAmount, orderQuantity, operatingCosts, bankLoanAmount, totalInvestment, c.* FROM (SELECT SUM(total_income) AS totalIncome, SUM(total_profit) AS totalProfit, SUM(total_tax) AS totalTax, SUM(emp_quantity) AS empQuantity, SUM(employee_compensation) AS employeeCompensation, SUM(loan_amount) AS loanAmount, SUM(order_quantity) AS orderQuantity, SUM(operating_costs) AS operatingCosts, SUM(bank_loan_amount) AS bankLoanAmount, SUM(total_investment) AS totalInvestment,
MONTH AS month area_name AS areaName type_name AS typeName
FROM statis_view WHERE `year` = #{year} AND `month` <= #{month}
AND type_name = #{typeName}
AND area_name = #{areaName}
AND company_Name like CONCAT(CONCAT('%', #{companyName}), '%')
AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY
MONTH area_name type_name
) n RIGHT JOIN (
SELECT MONTH AS name FROM statis_view WHERE `year` = #{year} AND `month` <= #{month} AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY MONTH
SELECT name FROM sys_area WHERE type != 1 AND type != 2
SELECT label AS name FROM sys_dict WHERE type=#{dictType}
) c ON
c.`name` = n.`month` c.`name` = n.`areaName` c.`name` = n.`typeName`

SELECT totalIncome, totalProfit, totalTax, empQuantity, employeeCompensation, loanAmount, orderQuantity, operatingCosts, bankLoanAmount, totalInvestment, c.* FROM (SELECT SUM(total_income) AS totalIncome, SUM(total_profit) AS totalProfit, SUM(total_tax) AS totalTax, SUM(emp_quantity) AS empQuantity, SUM(employee_compensation) AS employeeCompensation, SUM(loan_amount) AS loanAmount, SUM(order_quantity) AS orderQuantity, SUM(operating_costs) AS operatingCosts, SUM(bank_loan_amount) AS bankLoanAmount, SUM(total_investment) AS totalInvestment,
MONTH AS month area_name AS areaName type_name AS typeName
FROM statis_view WHERE `year` = #{year} AND `month` <= #{month}
AND type_name = #{typeName}
AND area_name = #{areaName}
AND company_Name like CONCAT(CONCAT('%', #{companyName}), '%')
AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY
MONTH area_name type_name
) n RIGHT JOIN (
SELECT MONTH AS name FROM statis_view WHERE `year` = #{year} AND `month` <= #{month} AND company_id in(select id from sys_office where parent_id=#{parentId}) GROUP BY MONTH
SELECT name FROM sys_area WHERE type != 1 AND type != 2
SELECT label AS name FROM sys_dict WHERE type=#{dictType}
) c ON
c.`name` = n.`month` c.`name` = n.`areaName` c.`name` = n.`typeName`



 select v2.area_name,ifnull(emp_quantity,0) emp_quantity from
	(select area_name,sum(emp_quantity) emp_quantity from statis_view 

		year=#{year}
		and month=#{month}
		and type_name=#{typeName}
		and company_Name like CONCAT(CONCAT('%', #{companyName}), '%')
		AND company_id in(select id from sys_office where parent_id=#{parentId})

		group by area_name
	) v1 right join 
	(select area_name from statis_view  group by area_name  )v2
on v1.area_name = v2.area_name

select v2.type_name,ifnull(emp_quantity,0)emp_quantity from
	(select type_name,sum(emp_quantity) emp_quantity from statis_view 

		year=#{year}
		and month=#{month}
		and area_name=#{areaName}
		and company_Name like CONCAT(CONCAT('%', #{companyName}), '%')
		AND company_id in(select id from sys_office where parent_id=#{parentId})

		group by type_name
	) v1 right join 
	(select distinct label type_name from sys_dict where type=#{dictType})v2
on v1.type_name = v2.type_name
