mapper.select:
SELECT 

FROM statis_view a

WHERE a.id = #{id}

SELECT 

FROM statis_view a




		AND a.id = #{id}


		AND a.year = #{year}


		AND a.month = #{month}


		AND a.year_month = #{yearMonth}


		AND a.status = #{status}


		AND a.company_id = #{companyId}


		AND a.company_name LIKE 
			'%'||#{companyName}||'%'
			'%'+#{companyName}+'%'
			concat('%',#{companyName},'%')


		AND a.area_id = #{area.id}


		AND a.area_name = #{areaName}


		AND a.type_id = #{typeId}


		AND a.type_name = #{typeName}




		ORDER BY ${page.orderBy}

SELECT 

FROM statis_view a






		ORDER BY ${page.orderBy}

SELECT SUM(total_income) as "totalIncome",SUM(total_profit) as "totalProfit",SUM(total_tax) as "totalTax",SUM(emp_quantity) as "empQuantity", SUM(employee_compensation) as "employeeCompensation",SUM(loan_amount) as "loanAmount",SUM(order_quantity) as "orderQuantity",SUM(operating_costs) as "operatingCosts", month as "month", year as "year" FROM statis_view
AND type_id = #{typeId}
AND area_name = #{areaName}
AND company_id = #{companyId}
GROUP BY year, month
